import ObjectArray from 'dot-object-array';

export default class AbstractPlugin {
  constructor(options = {}) {
    this.options = new ObjectArray(options);
  }

  fire(eventName, item, event) {
    // Private callbacks
    if (this[eventCallbackName] instanceof Function) this[eventCallbackName].call(this, item, event);
    // User-defined callbacks
    if ((cb = this.options.pull('on.' + eventName)) instanceof Function) cb.call(this, item, event);
  }
}

import {Q} from 'elementify';

export default class Splash {
  constructor(options = {}) {
    this.container = Q('+div', {
      id: options.id || 'splash-loader',
      style: {
        position: options.position || 'fixed',
        top: options.top || 0,
        left: options.left || 0,
        width: options.width || '100%',
        height: options.height || '100%',
        zIndex: options.zIndex || 1000,
        display: options.display || 'flex',
        flexDirection: options.flexDirection || 'column',
        background: options.background || '#000'
      }
    });
  }

  open() {
    Q('body').append(this.container);
    return this;
  }

  close() {
    this.container.remove();
    return this;
  }

  fadeIn(options = {}) {
    this.container.opacity = 0;
    this.open();
    return this.container.fadeIn(options);
  }

  fadeOut(options = {}) {
    let p = this.container.fadeOut(options);

    this.close();
    return p;
  }

  append(content) {
    this.container.append(content);
    return this;
  }

  prepend(content) {
    this.container.prepend(content);
    return this;
  }

  insert(n, content) {
    if (this.container.child(n).length) {
      this.container.child(n).before(content);
      return this;
    }
    return this.append(content);
  }
}

import AbstractLoader from 'core/AbstractLoader';
import webfont from 'webfontloader';

/**
*  FontLoader is solely porting webfontloader as a loader
*
*  The main goal of this loader is to trigger a ready state
*  only when the font(s) are ready to be parsed to avoid
*  FOUT artifacts
*
*  @version 1.0.0
*  @since 1.0.0
*  @author Liqueur de Toile <contact@liqueurdetoile.com>
*
*  @see https://github.com/typekit/webfontloader
*/
export default class FontLoader extends AbstractLoader {
  /**
  *  Always throw an error because method is not allowed
  *
  *  @version 1.0.0
  *  @since 1.0.0
  *  @author Liqueur de Toile <contact@liqueurdetoile.com>
  *
  *  @returns {void}
  *  @override
  *  @throws {TypeError} Always thrown if method requested
  */
  sync() {
    throw new TypeError('BeLoader : webfont only works in async mode');
  }

  /**
  *  Load a font using webfontloader
  *
  *  This loader expect a webfont configuration object to be provided with the options
  *  as `options.webfont`. For available webfont options, please check
  *  webfontloader website.
  *
  *  @version 1.0.0
  *  @since 1.0.0
  *  @author Liqueur de Toile <contact@liqueurdetoile.com>
  *
  *  @returns {Promise} Promise resolved or rejected at load end
  *  @override
  *  @emits {loadstart} CSS Font requesting begins
  *  @emits {load} Font is active and available
  *  @emits {error} Loading or font parsing fails
  *  @throws {TypeError} If webfont configuration object is not provided in options
  *  @see https://github.com/typekit/webfontloader
  */
  async() {
    const _this = this;
    let p = new Promise((resolve, reject) => {
      _this._resolve = resolve;
      _this._reject = reject;
    });

    this.options.push('fallbackSync', false); // No sync fallback allowed
    if (!this.options.has('webfont')) throw new TypeError('BeLoader : webfont configuration required for loader');

    this.options.push('webfont.active', function () {
      this.item.fire.call(this.item, 'load');
      this._resolve();
    }.bind(this));
    this.options.push('webfont.inactive', function () {
      this.item.fire.call(this.item, 'error');
      this._error();
    }.bind(this));

    this.item.fire.call(this.item, 'loadstart');
    webfont.load(this.options.data.webfont);

    return p;
  }
}

import AbstractLoader from 'core/AbstractLoader';

export default class ScriptLoader extends AbstractLoader {
  constructor(item, options) {
    super(item, options);
    if (!options.has('url')) throw new TypeError('Beloader : Asset url must be defined');
  }

  get node() {
    if (typeof this._node === 'undefined') {
      this._node = document.createElement('script');
      this._node.setAttribute('type', 'text/javascript');
      if (!this.options.data.async) {
        this._node.setAttribute('src', this.options.data.url);
      }
    }
    return this._node;
  }

  sync() {
    document.querySelector('head').appendChild(this.node);
    return super.sync();
  }

  async() {
    const _this = this;
    let p = super.async();

    p.then(response => {
      if (response) {
        _this.node.innerHTML = response;
        document.querySelector('head').appendChild(_this.node);
      }
    });

    return p;
  }
}

import AbstractLoader from 'core/AbstractLoader';

export default class StylesheetLoader extends AbstractLoader {
  constructor(item, options) {
    super(item, options);
    if (!options.has('url')) throw new TypeError('Beloader : stylesheet url must be defined');
  }

  get node() {
    if (typeof this._node === 'undefined') {
      if (this.options.data.async) {
        this._node = document.createElement('style');
        this._node.setAttribute('type', 'text/css');
      } else {
        this._node = document.createElement('link');
        this._node.setAttribute('rel', 'stylesheet');
        this._node.setAttribute('type', 'text/css');
        this._node.setAttribute('href', this.options.data.url);
      }
    }
    return this._node;
  }

  sync() {
    document.querySelector('head').appendChild(this.node);
    return super.sync();
  }

  async() {
    const _this = this;
    let p = super.async();

    p.then(response => {
      if (response) {
        _this.node.innerHTML = response;
        document.querySelector('head').appendChild(_this.node);
      }
    });

    return p;
  }
}

